"use client"

import type React from "react"

import { useState, useEffect } from "react"
import { Search, Filter, Check, ShoppingCart, Tag, Star } from "lucide-react"
import { useCart } from "@/context/cart-context"

interface Product {
  id: number
  name: string
  description: string
  price: number
  category: string
  image: string
  rating?: number
  features?: string[]
}

interface ProductsProps {
  visibleSections: string[]
  productsRef: React.RefObject<HTMLElement>
}

export default function Products({ visibleSections, productsRef }: ProductsProps) {
  const { addItem } = useCart()
  const [products, setProducts] = useState<Product[]>([
    {
      id: 1,
      name: "AI Content Generator",
      description: "Generate high-quality content for all your platforms",
      price: 49,
      category: "Content",
      image: "data:image/jpeg;base64,",
      rating: 4.8,
      features: ["Multi-platform support", "SEO optimization", "Tone customization"],
    },
    {
      id: 2,
      name: "Social Media Scheduler",
      description: "Schedule posts across all your social media accounts",
      price: 39,
      category: "Social",
      image: "https://images.unsplash.com/photo-1611162617474-5b21e879e113?q=80&w=800&auto=format&fit=crop",
      rating: 4.6,
      features: ["Visual calendar", "Analytics integration", "Auto-posting"],
    },
    {
      id: 3,
      name: "Email Campaign Manager",
      description: "Create and manage personalized email campaigns",
      price: 59,
      category: "Email",
      image: "data:image/jpeg;base64,",
      rating: 4.7,
      features: ["A/B testing", "Audience segmentation", "Template library"],
    },
    {
      id: 4,
      name: "Analytics Dashboard",
      description: "Track performance across all your marketing channels",
      price: 79,
      category: "Analytics",
      image: "https://images.unsplash.com/photo-1551288049-bebda4e38f71?q=80&w=800&auto=format&fit=crop",
      rating: 4.9,
      features: ["Real-time data", "Custom reports", "Goal tracking"],
    },
    {
      id: 5,
      name: "Audience Segmentation Tool",
      description: "Create targeted audience segments for better engagement",
      price: 69,
      category: "Analytics",
      image: "https://images.unsplash.com/photo-1551135049-8a33b5883817?q=80&w=800&auto=format&fit=crop",
      rating: 4.5,
      features: ["Behavioral targeting", "Demographic filters", "Engagement scoring"],
    },
    {
      id: 6,
      name: "Chatbot Builder",
      description: "Create AI-powered chatbots for customer engagement",
      price: 89,
      category: "Engagement",
      image: "https://images.unsplash.com/photo-1531747118685-ca8fa6e08806?q=80&w=800&auto=format&fit=crop",
      rating: 4.7,
      features: ["No-code builder", "AI responses", "Multi-channel deployment"],
    },
  ])

  const [filteredProducts, setFilteredProducts] = useState<Product[]>(products)
  const [searchTerm, setSearchTerm] = useState("")
  const [selectedCategories, setSelectedCategories] = useState<string[]>([])
  const [showFilters, setShowFilters] = useState(false)
  const [isVisible, setIsVisible] = useState(false)

  const categories = Array.from(new Set(products.map((product) => product.category)))

  useEffect(() => {
    let result = products

    // Apply search filter
    if (searchTerm) {
      result = result.filter(
        (product) =>
          product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          product.description.toLowerCase().includes(searchTerm.toLowerCase()),
      )
    }

    // Apply category filter
    if (selectedCategories.length > 0) {
      result = result.filter((product) => selectedCategories.includes(product.category))
    }

    setFilteredProducts(result)
  }, [searchTerm, selectedCategories, products])

  useEffect(() => {
    if (visibleSections.includes("products")) {
      setIsVisible(true)
    }
  }, [visibleSections])

  const toggleCategory = (category: string) => {
    if (selectedCategories.includes(category)) {
      setSelectedCategories(selectedCategories.filter((c) => c !== category))
    } else {
      setSelectedCategories([...selectedCategories, category])
    }
  }

  const handleAddToCart = (product: Product) => {
    addItem({
      id: product.id,
      name: product.name,
      price: product.price,
      image: product.image,
      category: product.category,
    })
  }

  return (
    <section ref={productsRef} className="py-12 md:py-20 scroll-mt-20 section-spacing overflow-fix">
      <div className="container mx-auto px-4 sm:px-6">
        <div
          className={`text-center mb-8 md:mb-16 fade-in ${isVisible ? "visible" : ""}`}
          style={{ transitionDelay: "0.2s" }}
        >
          <span className="inline-block px-4 py-1 bg-white/10 rounded-full text-[#f6d2b0] text-sm font-medium mb-4">
            Products
          </span>
          <h2 className="mb-4 section-title">Our Products</h2>
          <p className="text-base md:text-lg text-white/80 max-w-2xl mx-auto">
            Explore our range of AI-powered marketing tools to enhance your outreach strategy.
          </p>
        </div>

        <div
          className={`flex flex-col md:flex-row gap-4 sm:gap-6 mb-6 sm:mb-8 fade-in ${isVisible ? "visible" : ""}`}
          style={{ transitionDelay: "0.3s" }}
        >
          <div className="relative flex-grow">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-white/60" size={20} />
            <input
              type="text"
              placeholder="Search products..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full bg-white/10 border border-white/20 rounded-lg pl-10 pr-4 py-3 text-white focus:outline-none focus:ring-2 focus:ring-[#644caa]"
            />
          </div>

          <div className="md:w-auto">
            <button
              onClick={() => setShowFilters(!showFilters)}
              className="w-full md:w-auto flex items-center justify-center gap-2 bg-white/10 border border-white/20 rounded-lg px-4 py-3 text-white hover:bg-white/20 transition-colors"
            >
              <Filter size={20} className="text-[#f6d2b0]" />
              <span>Filter</span>
            </button>
          </div>
        </div>

        {showFilters && (
          <div
            className={`mb-6 sm:mb-8 glass-card p-4 rounded-lg fade-in ${isVisible ? "visible" : ""}`}
            style={{ transitionDelay: "0.4s" }}
          >
            <h3 className="text-white font-medium mb-4 flex items-center gap-2">
              <Tag size={18} className="text-[#f6d2b0]" />
              Categories
            </h3>
            <div className="flex flex-wrap gap-2 sm:gap-4">
              {categories.map((category, index) => (
                <div
                  key={index}
                  onClick={() => toggleCategory(category)}
                  className={`flex items-center gap-2 cursor-pointer px-3 sm:px-4 py-2 rounded-full ${
                    selectedCategories.includes(category)
                      ? "bg-[#644caa] text-white"
                      : "bg-white/10 text-white/80 hover:bg-white/20"
                  } transition-colors text-sm sm:text-base`}
                >
                  <div
                    className={`w-4 h-4 rounded border flex items-center justify-center ${
                      selectedCategories.includes(category) ? "border-white bg-[#644caa]" : "border-white/50"
                    }`}
                  >
                    {selectedCategories.includes(category) && <Check size={12} />}
                  </div>
                  <span>{category}</span>
                </div>
              ))}
            </div>
          </div>
        )}

        {filteredProducts.length === 0 ? (
          <div
            className={`text-center py-12 fade-in ${isVisible ? "visible" : ""}`}
            style={{ transitionDelay: "0.5s" }}
          >
            <p className="text-white/80 text-lg">No products found matching your criteria.</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6 md:gap-8">
            {filteredProducts.map((product, i) => (
              <div
                key={i}
                className={`glass-card rounded-xl overflow-hidden shadow-md hover:shadow-lg transition-shadow border-white/20 fade-in ${isVisible ? "visible" : ""} hover:border-[#f6d2b0]/30 group`}
                style={{ transitionDelay: `${0.2 * (i + 1) + 0.4}s` }}
              >
                <div className="h-40 sm:h-48 relative">
                  <img
                    src={product.image || "/placeholder.svg"}
                    alt={product.name}
                    className="object-cover absolute inset-0 h-full w-full group-hover:scale-105 transition-transform duration-500"
                  />
                  <div className="absolute top-2 right-2 bg-[#04050c]/80 backdrop-blur-sm px-2 py-1 rounded-full flex items-center gap-1">
                    <Star size={14} className="text-[#f6d2b0] fill-[#f6d2b0]" />
                    <span className="text-white text-xs font-medium">{product.rating}</span>
                  </div>
                </div>
                <div className="p-4 sm:p-6">
                  <div className="flex flex-wrap justify-between items-center mb-2 gap-2">
                    <h3 className="text-lg sm:text-xl font-bold text-white group-hover:text-[#f6d2b0] transition-colors">
                      {product.name}
                    </h3>
                    <span className="text-xs sm:text-sm font-medium px-2 sm:px-3 py-1 rounded-full bg-[#644caa]/30 text-[#f6d2b0]">
                      {product.category}
                    </span>
                  </div>
                  <p className="text-white/80 mb-4 text-sm sm:text-base">{product.description}</p>

                  {product.features && (
                    <div className="mb-4 space-y-2">
                      {product.features.map((feature, idx) => (
                        <div key={idx} className="flex items-center gap-2 text-sm text-white/70">
                          <Check size={14} className="text-[#f6d2b0]" />
                          <span>{feature}</span>
                        </div>
                      ))}
                    </div>
                  )}

                  <div className="flex flex-wrap justify-between items-center gap-2">
                    <span className="text-xl sm:text-2xl font-bold text-white">${product.price}</span>
                    <button
                      onClick={() => handleAddToCart(product)}
                      className="bg-[#04050c] text-white px-3 sm:px-4 py-2 rounded-full hover:bg-[#f6d2b0] hover:text-[#04050c] transition-colors flex items-center gap-2 text-sm sm:text-base group-hover:bg-[#f6d2b0] group-hover:text-[#04050c]"
                    >
                      <ShoppingCart size={16} />
                      <span>Add to Cart</span>
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </section>
  )
}
